import React, { Fragment } from 'react';

import Post from '../Post';

import './Timeline.scss';
import { Button } from '@material-ui/core';
import { fetchPosts } from '../../store/reducers/common';

const Timeline = ({ posts, readPosts, dismissAll }) =>Â {

  const getUnreadStatus = id => {
    return !readPosts.includes(id);
  }

  return (
    <Fragment>
      {
        posts.length ? posts.map((post, index) => (
          <Post
            key={index}
            unread={getUnreadStatus(post.data.id)} 
            post={post.data} 
          />
        )) :
        <div> 
          <div>
          "Theres no posts, click here to reload"
          </div>
          <div>
            <Button onClick={()=> fetchPosts()}>Reload posts</Button>
          </div>
        </div>
      }
      {
        !!posts.length &&
        <div
          className="dismiss-all" 
          onClick={()=> dismissAll()}
        >
        DISMISS ALL
        </div>
      }
    </Fragment>
  )
} 

export default Timeline;
