import React, { Fragment } from 'react';

import Post from '../Post';

const Timeline = ({ posts, readPosts }) =>Â {

  const getUnreadStatus = id => {
    return readPosts.includes(id);
  }

  return (
    <Fragment>
      {
        posts.map(post => (
          <Post
            unread={getUnreadStatus(post.id)} 
            post={post.data} 
          />
        ))
      }
    </Fragment>
  )
} 

export default Timeline;
