import axios from 'axios';

export const apiParser = (route, method, payload) => {
  const BASE_URL = `https://www.reddit.com/r/${route}/top.json`;

  switch(method) {
    //here you can add other methods you need later
    default: 
    return (
      axios.get(BASE_URL)
      .then()
      .catch()
    ) 
  }
};
